x=float(input("x:"))y=float(input("y:"))z=float(input("z:"))if (x+y>z and x+z>y and y+z>x):    if(x==y and y==z):        print("Deu")    else:        if((x*x==y*y+z*z) or (y*y==x*x+z*z) or (z*z==x*x+y*y)):            if(x==y or y==z or x==z):                print("Vuong can")            else:                print("Vuong")                        else:            if(x==y or y==z or x==z):                print("Can")            else:                print("Thuong")        else:    print("Ko")